import React from 'react';
import '../styles/UserProfile.css';

function UserProfile({ user }) {
  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="user-profile">
      <img src={user.avatarUrl || 'default-avatar.png'} alt="User Avatar" />
      <h2>{user.username || 'Unknown User'}</h2>
      <p><strong>Following:</strong> {user.followingCount || 0}</p>
      <p><strong>Followers:</strong> {user.followerCount || 0}</p>
      <p><strong>Likes:</strong> {user.likesCount || 0}</p>
      <button className="button" onClick={user.onGeneratePersonaAnalysis}>Generate Persona Analysis</button>
    </div>
  );
}

export default UserProfile;
