// src/components/OAuthCallback.js
import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const OAuthCallback = () => {
  const location = useLocation();

  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const authCode = queryParams.get('code');
    const error = queryParams.get('error');

    if (authCode) {
      // Handle the OAuth response and exchange the auth code for an access token
      console.log('Authorization code:', authCode);
      // Make a request to your backend server to exchange the code for an access token
      fetch('https://your-backend-server.com/oauth/token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ code: authCode })
      })
        .then(response => response.json())
        .then(data => {
          console.log('Access token:', data.access_token);
          // Handle the access token (e.g., store it in local storage or context)
        })
        .catch(error => console.error('Error exchanging code for token:', error));
    } else if (error) {
      console.error('Authorization error:', error);
    }
  }, [location]);

  return <div>Loading...</div>;
};

export default OAuthCallback;
