// src/components/UserProfile.js
import React from 'react';
import '../styles/UserProfile.css';

const UserProfile = ({ user }) => {
  const avatarUrl = user.avatarUrl || '/default-avatar.png';
  return (
    <div className="user-profile">
      <img src={avatarUrl} alt="User Avatar" className="avatar" />
      <h2>{user.username}</h2>
      <p>Following: {user.followingCount}</p>
      <p>Followers: {user.followerCount}</p>
      <p>Likes: {user.likesCount}</p>
      <button onClick={() => alert('Generating Persona Analysis...')}>Generate Persona Analysis</button>
    </div>
  );
};

export default UserProfile;
