import React from 'react';
import '../styles/GeneratedRecommendations.css';

function GeneratedRecommendations({ objective, recommendation }) {
  return (
    <div className="generated-recommendations">
      <h2>Generated Recommendations</h2>
      <p><strong>Based on your persona analysis and hottest trend analysis, we recommend this video content which will allow you to have the greatest following/video likes view:</strong></p>
      <h3>Video Idea</h3>
      <p>{recommendation.videoIdea}</p>
      <h3>Props</h3>
      <p>{recommendation.props}</p>
      <h3>Theme</h3>
      <p>{recommendation.theme}</p>
      <h3>Script</h3>
      <p>{recommendation.script}</p>
      <h3>Video Content</h3>
      <ul>
        {recommendation.videoContent.map((content, index) => (
          <li key={index}>
            <strong>{content.timeFrame}</strong>: {content.script} - {content.action}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default GeneratedRecommendations;
